import React, { useEffect, useState, useRef } from "react";
import { Component } from 'react'
import classes from './Posts.module.css';
import ScrollAnimation from 'react-animate-on-scroll';
import "animate.css/animate.min.css";
var data = require("./../../db/MOCK_DATA.json");

const Home = () => {

    const gameCompleted = useRef(false);
    const [audiolist,setAudiolist]= useState({});
   const key = 'blog_deets';






     //const audiolistCollectionRef= collection(db,"audiolist");







    return (

      <>
     <div className={classes.box} id="interest">
        <ScrollAnimation offset={0} animateIn="fadeInLeft" duration={2.4} animateOnce={true} initiallyVisible={true}>
          <span className={classes.head}>Random Stuff</span>
          <h2 className={classes.heading}>{audiolist.title}</h2>
          <div className={classes.Interest}>
            <ScrollAnimation offset={0} animateIn="fadeInLeft" duration={2.4} animateOnce={true} initiallyVisible={true}>
              <div className={classes.web}>
                <h3>{data.post1_title}</h3>
                <p>{data.post1_content} </p>
              </div>
            </ScrollAnimation>
            <ScrollAnimation offset={0} animateIn="fadeInLeft" duration={2.4} animateOnce={true} initiallyVisible={true}>
              <div className={classes.app}>
                <h3>Want to read another Book like 'Kiss Before Dying' before dying</h3>
                <p>There are two kinds of authors - ones who go on and on about the color of the sky, surroundings, dresses, appearences etc., and the other kind who prefer to spend time inside a characters head meddling with good and bad. The latter, my fav kind and this book my fav from that kind (yeah, he spent far too much time on copper mining in the end chapters but I think its justified in this case (shows us where truly Bud's heart lied all this time, dude never felt a single thing about any of the 3 sisters.)</p>
              </div>
            </ScrollAnimation>
            <ScrollAnimation offset={0} animateIn="fadeInLeft" duration={2.4} animateOnce={true} initiallyVisible={true}>
              <div className={classes.other}>
                <h3>Other's Interest</h3>
                <p>I also have decent hand in developing website with WordPress.<br />C++, Github are my other fields of interest. Currently, I am investing my free time in Web3.js </p>
              </div>
            </ScrollAnimation>
          </div>
        </ScrollAnimation>
      </div>
          </>
    );
  };
  export default Home;
